import React, {Component} from 'react';
import axios from 'axios';
import { BrowserRouter as Router } from 'react-router-dom';
import { GlobalStyle } from './globalStyles';
import Products from './components/Products/index';
import data from './components/Products/data'
import './App.css';

class App extends Component {

  constructor(){
    super();

    this.state = {
      cart: [],
      data: []
    }
  }


  componentDidMount(){
    axios.get('/api/products')
      .then(res => {
        this.setState({data: res.data})
      })
      .catch()
  }

  addItem = (img, name, quantity) => {
    // console.log(name, quantity)

    axios.post('/api/cart', {img, name, quantity}).then((res) => {
      this.setState({
        cart: res.data,
      })
    })
    .catch(err => console.log(err))
  }

  removeItem = () => {

  }

  editItem = (id, quantity) => {
    axios.put(`/api/cart/${id}`, {quantity})
      .then(res => {
        this.setState({
          cart: res.data
        })
      })
      .catch(err => console.log(err))
  }

  render(){
    console.log(this.state.cart + "in app.js")
    return (
      <Router>
        <GlobalStyle />
        <Products editItem={this.editItem} cart={this.state.cart} addItem={this.addItem} data={this.state.data}/>
      </Router>
    );
  }

}

export default App;
