import React, { Component } from "react";
import {
  SidebarContainer,
  Icon,
  CloseIcon,
  SidebarRoute,
  SideBtnWrap,
} from "./SidebarElement";

class Sidebar extends Component {
  constructor(){
    super(); 

    this.state = {
      quantityInput: null,
    }

  }

  // console.log(cart)

  handleChange = (e) => {
    this.setState({quantityInput: e.target.value})
  }

  render(){

    const {cart, isOpen, toggle, editItem} = this.props;

    return (
      <SidebarContainer isOpen={isOpen} onClick={toggle}>
        <Icon>
          <CloseIcon />
        </Icon>
        {cart.map((prod, index) => {
          return (
            <div>
              <h1>{prod.name}</h1>
              <h2>{prod.quantity}</h2> 
              <input value={this.state.quantityInput} placeholder="Quantity" onChange={(e) => handleChange(e)}/>
              {/* the value let's you change the value that's INSIDE the input */}
              {/* onChange will change the STATE through the handleChange */}
              <button >Save</button>
            </div>
          )
        })}
        <SideBtnWrap>
          <SidebarRoute to="/">Order Now</SidebarRoute>
        </SideBtnWrap>
      </SidebarContainer>
    );
  }

};

export default Sidebar;
