import React, { Component } from "react";
import {
  SidebarContainer,
  Icon,
  CloseIcon,
  SidebarRoute,
  SideBtnWrap,
} from "./SidebarElement";

class Sidebar extends Component {
  constructor(props){
    super(props); 

    this.state = {
      quantityInput: null,
    }

  }

  // console.log(cart)

  handleChange = (e) => {
    this.setState({quantityInput: e.target.value})
  }

  render(){
    console.log(this.props)

    const {cart, isOpen, toggle, editItem} = this.props;

    return (
      <SidebarContainer isOpen={isOpen}>
        <Icon onClick={toggle}>
          <CloseIcon />
        </Icon>
        {cart.map((prod, index) => {

          const {name, quantity, id} = prod;

          return (
            <div>
              <h1>{name}</h1>
              <h2>{quantity}</h2> 
              <input value={this.state.quantityInput} placeholder="Quantity" onChange={(e) => this.handleChange(e)}/>
              {/* the value let's you change the value that's INSIDE the input */}
              {/* onChange will change the STATE through the handleChange */}
              <button onClick={() => editItem(id, this.state.quantityInput)}>Save</button>
            </div>
          )
        })}
        <SideBtnWrap>
          <SidebarRoute to="/">Order Now</SidebarRoute>
        </SideBtnWrap>
      </SidebarContainer>
    );
  }

};

export default Sidebar;
