import { createGlobalStyle } from 'styled-components'; 

export const GlobalStyle = createGlobalStyle`
  * {
    box-sizing: border-box; 
    margin: 0; 
    padding: 0; 
    font-family: 'Kanit', sans-serif; 
  }
  html { 
    width: 100vw;
    background-image: linear-gradient(
      to right, 
      #dddddd,
      #f2b4b4
    )
  }
`
