import React, {useState} from 'react';
import {
  ProductsContainer,
  ProductsWrapper,  
  ProductTitle,
  ProductCard,
  ProductImg,
  ProductInfo,
  ProductDesc,
  ProductPrice,
  ProductButton
} from './ProductElements';
import Sidebar from '../Sidebar';
import Navbar from '../Navbar'
// import img from '../../images/CFP-counter.jpg'

const Products = (props) => {

  const {addItem, data} = props;

  const [isOpen, setIsOpen] = useState(false) 
          //This has to be an array. A little different from destructuring where you would use curly brackets
  const toggle = () => {
      setIsOpen(!isOpen) //We DON'T want it to be open to begin with that's why we put !isOpen
    }

  // console.log(props.data)


  return (
    <ProductsContainer>
      <Navbar toggle={toggle}/>
      <Sidebar isOpen={isOpen} toggle={toggle} />
      <ProductsWrapper>
        {data.map((prod, index) => {
          return (
            <ProductCard key={index}>
              <ProductImg src={require("../../images/CFP-counter.jpg")} alt={prod.alt}/>
              <ProductInfo>
                <ProductTitle>{prod.name}</ProductTitle>
                <ProductDesc>{prod.desc}</ProductDesc>
                <ProductPrice>{prod.price}</ProductPrice>
                <ProductButton onClick={() => addItem(prod.name, 1)}>{prod.button}</ProductButton>
              </ProductInfo>
            </ProductCard>
          )
        })}
      </ProductsWrapper>
    </ProductsContainer>
  )
}

export default Products
