import React, {useState} from 'react'
import Navbar from '../Navbar'
import {HeroContainer, HeroContent, HeroItems, HeroH1, HeroP, HeroBtn} from './HeroElements';
import Sidebar from '../Sidebar'
import mainImg from '../../images/cfp-cover-1.jpg'

const Hero = () => {
  const [isOpen, setIsOpen] = useState(false) 
        //This has to be an array. A little different from destructuring where you would use curly brackets
  const toggle = () => {
    setIsOpen(!isOpen) //We DON'T want it to be open to begin with that's why we put !isOpen
  }
  return (
    <HeroContainer>
      <Navbar toggle={toggle}/>
      <Sidebar isOpen={isOpen} toggle={toggle} />
      <HeroContent>
        <HeroItems>
          <HeroH1><img className="mainImg" src={mainImg} alt="Logo"/></HeroH1>
          <HeroP></HeroP>
          <HeroBtn>Place Order</HeroBtn>
        </HeroItems>
      </HeroContent>
    </HeroContainer>
  )
}

export default Hero
